INSERT INTO vets_database.Section(`Id`,`ContentCycle_Id`,`Title`,`Main_Idea`,`Passage_Paraphrased`,`Passage_Reference`,`Additional_Thoughts`,`Song_Link`,`Order`) VALUES (1,1,'Daily Attitude of my Heart - Sacrifice','I present myself as a living sacrifice before YOU, Spirit fill & control me.','I present my body as a living sacrifice, holy & acceptable to YOU, which is my spiritual worship & reasonable service.','Romans 12:1-2','God is attracted to weakness. He can’t resist those who humbly and honestly admit how desperately they need Him. Jim Cymbola','https://www.youtube.com/watch?v=NvAiO12cAQs',1);
INSERT INTO vets_database.Section(`Id`,`ContentCycle_Id`,`Title`,`Main_Idea`,`Passage_Paraphrased`,`Passage_Reference`,`Additional_Thoughts`,`Song_Link`,`Order`) VALUES (2,1,'Daily Attitude of my Heart - Focus','Deny my self centered life. I recognize that I need to deny my own "justified" feelings and rights.','YOU have told me that if I wish to follow YOU, I must deny myself, take up my cross daily & follow YOU.','Luke 9:23',NULL,'https://www.youtube.com/watch?v=Tw8-g7a3T7k',2);
