INSERT INTO vets_database.Section(`Id`,`ContentCycle_Id`,`Title`,`Main_Idea`,`Passage_Paraphrased`,`Passage_Reference`,`Additional_Thoughts`,`Song_Link`,`Order`) VALUES (28,1,'Thanks',NULL,'Let YOUR peace rule in my heart, which I was called in one body to be thankful. Let YOUR word dwell in me richly, teaching & admonishing me in all wisdom, singing psalms & hymns & spiritual songs, with thankfulness in my heart to YOU. And whatever I do, in word or deed, may I do everything in YOUR name Lord Jesus, giving thanks to YOU God the Father.','Col 3:15-17',NULL,NULL,28);
