INSERT INTO vets_database.Section(`Id`,`ContentCycle_Id`,`Title`,`Main_Idea`,`Passage_Paraphrased`,`Passage_Reference`,`Additional_Thoughts`,`Song_Link`,`Order`) VALUES (57,2,'Pray for salvation of others',NULL,'For YOUR invisible attributes, namely, YOUR eternal power & divine nature, have been clearly perceived, ever since the creation of the world, in the things that have been made. So, I am without excuse.','Rom 1:20-22','power of Gospel into salvation, For I am not ashamed of the gospel, because it is the power of God that brings salvation to everyone who believes: first to ?, then to ?.',NULL,17);
