INSERT INTO vets_database.Section(`Id`,`ContentCycle_Id`,`Title`,`Main_Idea`,`Passage_Paraphrased`,`Passage_Reference`,`Additional_Thoughts`,`Song_Link`,`Order`) VALUES (121,4,'Daily Attitude of my Heart - Sacrifice','I present myself as a living sacrifice, Help me life in the power of your grace today JESUS','For you do not delight in sacrifice, or I would give it; The sacrificial gift YOU desire from me is my broken spirit. You will not reject a broken & repentant heart, O God. Once I fully give YOU that, YOUR reinfocments, supplies & relief are heading my way!','Ps 51:16, 17',NULL,'Matt Redman - Gracefully Broken (Lyric Video) ft. Tasha Cobbs Leonard',1);
