INSERT INTO vets_database.Section(`Id`,`ContentCycle_Id`,`Title`,`Main_Idea`,`Passage_Paraphrased`,`Passage_Reference`,`Additional_Thoughts`,`Song_Link`,`Order`) VALUES (136,4,'Pray for One Another','I accept others just as YOU have accepted me so that YOU will be given glory.','Thank YOU for continuing to deliver & draw us to Yourself out of the control & the dominion of darkness & transfer us into the kingdom of YOUR Son, in whom we have our redemption through JESUS'' blood, the forgiveness of our sins.','Rom 15:7','I will be accepting of others.',NULL,16);
