INSERT INTO vets_database.Section(`Id`,`ContentCycle_Id`,`Title`,`Main_Idea`,`Passage_Paraphrased`,`Passage_Reference`,`Additional_Thoughts`,`Song_Link`,`Order`) VALUES (139,4,'Kids in my life (current or future).','For all the kids, may their spouses, current & those to come, be strong believers drawing them closer to YOU & be full of love for the larger family unit.','May my kids walk in YOUR CONTENTMENT -Keep their life free from love of money, and help them be content with what they have, for YOU tell us that YOU will never leave us nor forsake us.','Heb 13:5',NULL,NULL,19);
INSERT INTO vets_database.Section(`Id`,`ContentCycle_Id`,`Title`,`Main_Idea`,`Passage_Paraphrased`,`Passage_Reference`,`Additional_Thoughts`,`Song_Link`,`Order`) VALUES (140,4,'Pray for:','Peace of Jerusalem','I pray for the peace of Jerusalem: and they who love YOU will prosper. May peace be within its walls, and prosperity within the palaces.','Ps. 122:6-7',NULL,NULL,20);
