INSERT INTO vets_database.Section(`Id`,`ContentCycle_Id`,`Title`,`Main_Idea`,`Passage_Paraphrased`,`Passage_Reference`,`Additional_Thoughts`,`Song_Link`,`Order`) VALUES (202,6,'Daily Attitude of my Heart - Focus',NULL,'When I compete I exercise self-control in all things to receive a perishable prize. I will run in such a way, as not without aim; I box in such a way, as not beating the air; but I discipline my body & make it my slave. Help me do the same spiritually, so that, after I have preached to others, I myself will not be disqualified for lack of discipline and focus.','1 Corinthians 9:25-27',NULL,NULL,2);
