INSERT INTO vets_database.Section(`Id`,`ContentCycle_Id`,`Title`,`Main_Idea`,`Passage_Paraphrased`,`Passage_Reference`,`Additional_Thoughts`,`Song_Link`,`Order`) VALUES (213,6,'Repent','Then I acknowledged my sin to YOU and did not cover up my iniquity. I said, “I will confess my transgressions to the Lord.” And YOU forgave the guilt of my sin.  Psalm 25:18','YOU fulfill the desire of those who fear YOU; YOU also hear their cry and saves them.','Psalm 145:19 ESV','Forgive my sins in the same way & to the same degree as I forgive those that I feel have wronged and hurt me.  Mat 6:12',NULL,13);
INSERT INTO vets_database.Section(`Id`,`ContentCycle_Id`,`Title`,`Main_Idea`,`Passage_Paraphrased`,`Passage_Reference`,`Additional_Thoughts`,`Song_Link`,`Order`) VALUES (214,6,'Release','YOU will cast my sin as far as the East is from west (infinate).','Sow for yourselves righteousness; Reap in mercy; Break up your fallow ground, For it is time to seek the LORD, Till He comes and rains righteousness on you.','Hosea 10:12 NKJV',NULL,NULL,14);
