INSERT INTO vets_database.Section(`Id`,`ContentCycle_Id`,`Title`,`Main_Idea`,`Passage_Paraphrased`,`Passage_Reference`,`Additional_Thoughts`,`Song_Link`,`Order`) VALUES (388,10,'Thanks',NULL,'I give thanks to YOU, for YOU are good, for YOUR steadfast love endures forever! Let those YOU redeemed say so, whom YOU have redeemed from trouble.','Ps 107:1',NULL,NULL,28);
