INSERT INTO vets_database.Section(`Id`,`ContentCycle_Id`,`Title`,`Main_Idea`,`Passage_Paraphrased`,`Passage_Reference`,`Additional_Thoughts`,`Song_Link`,`Order`) VALUES (393,10,'Benidiction for my day',NULL,'And let YOUR peace rule in my heart, to which indeed I was called in one body & I am thankful. Let YOUR word dwell in me richly, teaching & admonishing others in all wisdom, singing psalms, hymns & spiritual songs, with thankfulness in my heart to YOU. And whatever I do, in word or deed, I will do everything in YOUR name Lord Jesus, giving thanks to God the FATHER through YOU.','Col 3:15-17',NULL,NULL,33);
