INSERT INTO vets_database.Section(`Id`,`ContentCycle_Id`,`Title`,`Main_Idea`,`Passage_Paraphrased`,`Passage_Reference`,`Additional_Thoughts`,`Song_Link`,`Order`) VALUES (610,16,'Wait on YOU','Let paitence have her perfect work.','And Moses said to the people, "Fear not, stand firm, and see the salvation of the Lord , which he will work for you today. For the Egyptians whom you see today, you shall never see again. The Lord will fight for you, and you have only to be silent."','Exodus 14:13‭-‬14 ESV',NULL,NULL,10);
