INSERT INTO vets_database.Section(`Id`,`ContentCycle_Id`,`Title`,`Main_Idea`,`Passage_Paraphrased`,`Passage_Reference`,`Additional_Thoughts`,`Song_Link`,`Order`) VALUES (694,18,'Release','YOU wrote to me as a dear child, because my sins have been forgiven on the account of YOUR name.  1 John 2:12','Treasures of wickedness profit nothing, But righteousness delivers from death. The LORD will not allow the righteous soul to famish, But He casts away the desire of the wicked.','Proverbs 10:2-3 NKJV',NULL,NULL,14);
