INSERT INTO vets_database.Section(`Id`,`ContentCycle_Id`,`Title`,`Main_Idea`,`Passage_Paraphrased`,`Passage_Reference`,`Additional_Thoughts`,`Song_Link`,`Order`) VALUES (708,18,'Thanks',NULL,'Behold, YOU are my salvation; I will trust, & will not be afraid; for YOU are my strength & my song, & YOU are my salvation." With joy, I will draw water from the wells of salvation. & I will say in that day: "Give thanks to YOU, call upon YOUR name, make known YOUR deeds among the people, proclaim that YOUR name is exalted. "Sing praises to YOU, for YOU have done gloriously; let this be made known in all the earth.','Is 12:2-5',NULL,NULL,28);
