INSERT INTO vets_database.Section(`Id`,`ContentCycle_Id`,`Title`,`Main_Idea`,`Passage_Paraphrased`,`Passage_Reference`,`Additional_Thoughts`,`Song_Link`,`Order`) VALUES (721,19,'Daily Attitude of my Heart - Sacrifice','I present myself as a living sacrifice, Help me life in the power of your grace today JESUS','May this mind be in me, which is in Christ Jesus, who, though HE was in the form of God, did not count equality with God a thing to be grasped, but made HIMself nothing, taking the form of a servant, being born in the likeness of men. And being found in human form, HE humbled himself by becoming obedient to the point of death, even death on a cross.','Philippians 2:5-8',NULL,NULL,1);
