INSERT INTO vets_database.Section(`Id`,`ContentCycle_Id`,`Title`,`Main_Idea`,`Passage_Paraphrased`,`Passage_Reference`,`Additional_Thoughts`,`Song_Link`,`Order`) VALUES (723,19,'Daily Attitude of my Heart - Obey','Read more: https://www.christianquotes.info/quotes-by-author/dwight-l-moody-quotes/#ixzz5NLKsYVEC','I show YOU my sincere love by keeping YOUR commandments, and they are not a burden.',NULL,NULL,NULL,3);
INSERT INTO vets_database.Section(`Id`,`ContentCycle_Id`,`Title`,`Main_Idea`,`Passage_Paraphrased`,`Passage_Reference`,`Additional_Thoughts`,`Song_Link`,`Order`) VALUES (724,19,'Daily Attitude of my Heart - Thoughts','I arrest all negative thoughts and turn them over to YOUR custody .','I choose to be anxious for nothing, but in everything by prayer & supplication with thanksgiving I will let my requests be made known to YOU so YOUR peace, which surpasses all comprehension, will guard my heart & mind in Christ Jesus.','Philippians 4:6-7','Take thoughts & feelings captive.',NULL,4);
INSERT INTO vets_database.Section(`Id`,`ContentCycle_Id`,`Title`,`Main_Idea`,`Passage_Paraphrased`,`Passage_Reference`,`Additional_Thoughts`,`Song_Link`,`Order`) VALUES (725,19,'My Father in Heaven','Thank YOU for adopting me into YOUR forever family because of my faith in JESUS.','How great is the love the Father has lavished on me, that I should be called a child of God! And that is who I am!','1Jn 3:1',NULL,NULL,5);
INSERT INTO vets_database.Section(`Id`,`ContentCycle_Id`,`Title`,`Main_Idea`,`Passage_Paraphrased`,`Passage_Reference`,`Additional_Thoughts`,`Song_Link`,`Order`) VALUES (726,19,'I exalt & glorify YOUR holy Name','YOU are: JEHOVAH TSIDKENU: "The Lord Our Righteousness"','JESUS gave me HIS robes of righteousness to cover my failures. I am holy and acceptable because YOU have made me that way through my faith in JESUS as my Lord and Savior.',NULL,NULL,NULL,6);
INSERT INTO vets_database.Section(`Id`,`ContentCycle_Id`,`Title`,`Main_Idea`,`Passage_Paraphrased`,`Passage_Reference`,`Additional_Thoughts`,`Song_Link`,`Order`) VALUES (727,19,'YOUR Nature','I worship because YOU are: My Protector & Rewarder','Answer me LORD in the day of my trouble! May the name of the God of Jacob protect me! May he grant me my heart''s desire & fulfill all my plans!','Ps 20:1, 4',NULL,NULL,7);
